from picamera.array import PiRGBArrayfrom picamera import PiCameraimport timeimport cv2import numpy as npcamera = PiCamera()camera.resolution = (640, 480)camera.framerate = 30rawCapture = PiRGBArray(camera, size = (640, 480))count=0minarea=500color=(255,0,0)nplatecascade = cv2.CascadeClassifier('haarcascade_russian_plate_number.xml') #location of xml filefirstFrame = Nonetime.sleep(2)for f in camera.capture_continuous(rawCapture, format="bgr", use_video_port=True):    frame = f.array    imggray = cv2.cvtColor(frame, cv2.COLOR_RGB2GRAY)    numberplates = nplatecascade.detectMultiScale(imggray, 1.1, 4) #image,scale factor, min neighbours    for (x, y, w, h) in numberplates:        area=w*h        if area>minarea:            count+=1            cv2.rectangle(frame, (x, y), (x + w, y + h), (255, 0, 0),1)            cv2.putText(frame,"Number Plate",(x,y-5),cv2.FONT_HERSHEY_SIMPLEX,1,color,2)#1-font scale; 2-font thickness            print("Vehicle detected "+str(count)+" times")    cv2.imshow("Result",frame)    rawCapture.truncate(0)# clear the stream in preparation for the next frame    key = cv2.waitKey(1) & 0xFF    if key == ord('q'):        breakcv2.destroyAllWindows()